SokolEffect sokol_init_fog(
    int width, int height)
{
    ecs_trace("sokol: initialize fog effect");

    SokolEffect fx = sokol_effect_init(2);

    sokol_effect_add_pass(&fx, (sokol_fx_pass_desc_t){
        .width = width, 
        .height = height, 
        .shader = shd_fog,
        .shader_header = shd_fog_hdr,
        .inputs = {
            [0] = { .name = "tex", .id = 0 },
            [1] = { .name = "depth", .id = 1 }
        }
    });

    return fx;
}


